	document.getElementById("blink1").style.visibility = "visible";
	settime-out("hide()", 450);
}
// 点滅「off」状態
function hide()
{
	if (document.getElementById)
	document.getElementById("blink1").style.visibility = "hidden";
	settime-out("show()", 450);
}
// 開始
show();
